function AjoutAdherentVue(){this.mCommunVue=new CommunVue();this.affect=function(){this.boutonLienCompte();this.mCommunVue.comNumeric()};this.boutonLienCompte=function(){$(":input[name=lien_numero_compte]").click(function(){if($(":input[name=numero_compte]").attr("disabled")){$(":input[name=numero_compte]").removeAttr("disabled")}else{$(":input[name=numero_compte]").attr("disabled","disabled").val("")}})}}$(document).ready(function(){var a=new AjoutAdherentVue();a.affect()});